from collections import Counter

# Absolutely ugly solution
class Solution:
    def maxPartitionsAfterOperations(self, s: str, k: int) -> int:

        def count_parts(s:str) -> int:
            parts = 0; chars = ''
            while s:
                parts +=1
                for idx, c in enumerate(s):
                    if c not in chars:
                        chars+=c
                        if len(chars) > k:
                            chars = ''
                            s = s[idx:]
                            break
                else:
                    s = ''
            return parts

        if len(s) <= k:
            return 1
        base_freq = Counter(s)
        if len(base_freq) < k :
            return 1
        if len(base_freq) == 26:
            all_letters = 'abcdefghijklmnopqrstuvwxyz'
        else:
            all_letters = '.'
        if k == 1:
#            print(s)
            if len(base_freq) == len(s):
                return len(base_freq)
            for c in base_freq:
                if c+c+c in s:
                    s = s.replace(c+c+c, c+'.'+c,1)
                    return count_parts(s)
            for c in base_freq:
                if c+c in s:
                    s = s.replace(c+c, c+'.',1)
                    return count_parts(s)

            return len(s)

        def examine(s:str) -> dict:
            max_x = len(s)-1
            ranges = []
            chars = Counter()
            cur_start = 0
            for idx, c in enumerate(s):
                chars[c] +=1
                if (len(chars) == k and (idx<max_x and s[idx+1] not in chars)) or (idx == max_x):
                    if idx ==  max_x:
                        idx+1
                    if len(chars) >1:
                        (_, freq1), (_,freq2) = chars.most_common(2)
                    else:
                        _, freq1 = chars.most_common(1)[0]
                        freq2 = 0
                    ranges.append((freq1, freq2, cur_start, idx, chars))
                    chars = Counter(c)
                    cur_start = idx
                
            return ranges

#        print(s)
        ranges = examine(s)
        ranges.sort(key = lambda x:(x[0],x[1]), reverse=True)
#        print(ranges)
        base_parts = count_parts(s)
#        print('base is', base_parts)
        max_parts = base_parts

        limit = 100
        for range_ in ranges:
            limit -= 1
            if limit == 0:
                return max_parts
#            print(range_)
            freq1, _,  start, end, chars = range_
            if freq1 == 1:
                continue
#            print(len(chars))
            for c in all_letters:
#                print(c, all_letters)
                if c not in chars:
                    subst = c
                    break
                else:
                    subst = ''
            if not subst:
                continue
            for idx in range(start, end):
                sx = s[:idx]+ subst +s[idx+1:]
                parts = count_parts(sx)
#                print(sx, parts)
                if parts> max_parts:
                    max_parts = parts
                if max_parts >= base_parts+2:
                    break

        return max_parts



engine = Solution()

